import React, { useState, useEffect } from "react";
import axios from "axios";
import * as XLSX from "xlsx";

export default function App() {
  const [fileData, setFileData] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    // Function to fetch data from the REST endpoint
    const fetchData = async () => {
      try {
        if (selectedFile) {
          const formData = new FormData();
          formData.append("my_audio_file", selectedFile);

          const response = await axios.post(
            "http://localhost:8086/api/file_tempo",
            formData,
            {
              headers: {
                "Content-Type": "multipart/form-data",
              },
            }
          );

          setFileData((prevData) => [...prevData, response.data]);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    // Call the fetchData function when the component mounts
    fetchData();
  }, [selectedFile]); // Run the effect whenever the selectedFile changes

  // Function to handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Function to export data to Excel
  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(fileData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "FileTempoData");
    XLSX.writeFile(wb, "file_tempo_data.xlsx");
  };

  return (
    <div className="App">
      <input type="file" onChange={handleFileChange} />
      <button onClick={exportToExcel}>Export to Excel</button>
      <table>
        <thead>
          <tr>
            <th>Filename</th>
            <th>Overall Tempo</th>
            <th>Peak 1</th>
            <th>Peak 2</th>
          </tr>
        </thead>
        <tbody>
          {fileData.map((data, index) => (
            <tr key={index}>
              <td>{data.filename}</td>
              <td>{data.overall_tempo}</td>
              <td>{data.peak_1}</td>
              <td>{data.peak_2}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
